#else
d=wgs84ToWebMerc(d);f=wgs84ToWebMerc(f);g=(d+f)*0.5;h=vec3(0.0,0.0,1.0);i=d-f;k=length(i);g=g+h*(k*0.3);if(k<=1000.0){l=48.0;}else if(k<=10000.0){l=42.0;}else if(k<=100000.0){l=32.0;}else if(k<=1000000.0){l=24.0;}else if(k<=2000000.0){l=36.0;}else{l=floor(k*0.000006);}if(drawPath){n=$auxpos1.z;}else{n=mod($auxpos1.z+(l*2.0+1.0)/il*mp,l*2.0+1.0);}if((n>=l)&&(n<(l*2.0+1.0))){m=-1.0;n-=l;}vec3 o=normalize(i);vec3 p=g+o*(k*0.3*m);float q=n/l;q=clamp(q,0.0,1.0);if(m<=0.0){j=getPoint(q,g,p,f,f);}else{j=getPoint(q,d,d,p,g);}gg=vec3(0.0,0.0,1.0);
